contracts/Banks/BankBase.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "../libraries/SaferERC20.sol";
import "../utils/OwnableUpgradeable.sol";

abstract contract BankBase is Initializable, OwnableUpgradeable {
    using SaferERC20 for IERC20;

    event Mint(uint256 tokenId, address userAddress, uint256 amount);
    event Burn(uint256 tokenId, address userAddress, uint256 amount, address receiver);
    event Harvest(uint256 tokenId, address userAddress, address receiver);

    address positionsManager;
    uint256[49] __gap;

    function __BankBase_init(address _positionsManager) public onlyInitializing {
        positionsManager = _positionsManager;
        __Ownable_init();
    }

    modifier onlyAuthorized() {
        require(msg.sender == positionsManager || msg.sender == currentOwner(), "1");
        _;
    }

    function name() external pure virtual returns (string memory);

    function getIdFromLpToken(address lpToken) external view virtual returns (bool, uint256);

    function decodeId(uint256 id) external view virtual returns (address, address, uint256);

    function getUnderlyingForFirstDeposit(
        uint256 tokenId
    ) public view virtual returns (address[] memory underlying, uint256[] memory ratios) {
        underlying = new address[](1);
        underlying[0] = getLPToken(tokenId);
        ratios = new uint256[](1);
        ratios[0] = 1;
    }

    function getUnderlyingForRecurringDeposit(
        uint256 tokenId
    ) external view virtual returns (address[] memory, uint256[] memory ratios) {
        return getUnderlyingForFirstDeposit(tokenId);
    }

    function getLPToken(uint256 tokenId) public view virtual returns (address);

    function getRewards(uint256 tokenId) external view virtual returns (address[] memory rewardsArray) {
        return rewardsArray;
    }

    receive() external payable {}

    function getPendingRewardsForUser(
        uint256 tokenId,
        address user
    ) external view virtual returns (address[] memory rewards, uint256[] memory amounts) {}

    function getPositionTokens(
        uint256 tokenId,
        address user
    ) external view virtual returns (address[] memory tokens, uint256[] memory amounts);

    function mint(
        uint256 tokenId,
        address userAddress,
        address[] memory suppliedTokens,
        uint256[] memory suppliedAmounts
    ) public virtual returns (uint256);

    function mintRecurring(
        uint256 tokenId,
        address userAddress,
        address[] memory suppliedTokens,
        uint256[] memory suppliedAmounts
    ) external virtual returns (uint256) {
        return mint(tokenId, userAddress, suppliedTokens, suppliedAmounts);
    }

    function burn(
        uint256 tokenId,
        address userAddress,
        uint256 amount,
        address receiver
    ) external virtual returns (address[] memory, uint256[] memory);

    function harvest(
        uint256 tokenId,
        address userAddress,
        address receiver
    ) external virtual onlyAuthorized returns (address[] memory rewardAddresses, uint256[] memory rewardAmounts) {
        return (rewardAddresses, rewardAmounts);
    }

    function isUnderlyingERC721() external pure virtual returns (bool) {
        return false;
    }
}

contracts/Banks/ERC20Bank.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.17;

import "./BankBase.sol";
import "../interfaces/IPositionsManager.sol";
import "hardhat/console.sol";

contract ERC20Bank is Initializable, ERC1155Upgradeable, BankBase {
    using SaferERC20 for IERC20;

    struct PoolInfo {
        mapping(address => uint256) userShares;
    }

    mapping(uint256 => PoolInfo) poolInfo;
    mapping(address => uint256) balances;

    function initialize(address _positionsManager) public initializer {
        __BankBase_init(_positionsManager);
        __ERC1155_init("ERC20Bank");
    }

    function encodeId(address tokenAddress) public pure returns (uint256) {
        return uint256(uint160(tokenAddress));
    }

    function decodeId(uint256 id) public pure override returns (address, address, uint256) {
        return (address(uint160(id)), address(0), 0);
    }

    function getLPToken(uint256 id) public pure override returns (address tokenAddress) {
        (tokenAddress, , ) = decodeId(id);
    }

    function getIdFromLpToken(address lpToken) external view override returns (bool, uint256) {
        if (lpToken == address(0) || lpToken == IPositionsManager(positionsManager).networkToken())
            return (true, encodeId(lpToken));
        try ERC20(lpToken).name() {} catch {
            return (false, 0);
        }
        try ERC20(lpToken).totalSupply() {} catch {
            return (false, 0);
        }
        try ERC20(lpToken).balanceOf(address(0)) {} catch {
            return (false, 0);
        }
        try ERC20(lpToken).decimals() {} catch {
            return (false, 0);
        }
        return (true, encodeId(lpToken));
    }

    function name() public pure override returns (string memory) {
        return "ERC20 Bank";
    }

    function getPositionTokens(
        uint256 tokenId,
        address userAddress
    ) external view override returns (address[] memory outTokens, uint256[] memory tokenAmounts) {
        (address lpToken, , ) = decodeId(tokenId);
        uint256 amount = balanceOf(userAddress, tokenId);
        outTokens = new address[](1);
        tokenAmounts = new uint256[](1);
        outTokens[0] = lpToken;
        tokenAmounts[0] = amount;
    }

    function mint(
        uint256 tokenId,
        address userAddress,
        address[] memory suppliedTokens,
        uint256[] memory suppliedAmounts
    ) public override onlyAuthorized returns (uint256) {
        PoolInfo storage pool = poolInfo[tokenId];
        pool.userShares[userAddress] += suppliedAmounts[0];
        _mint(userAddress, tokenId, suppliedAmounts[0], "");

        // Sanity check
        if (suppliedTokens[0] != address(0)) {
            require(
                balances[suppliedTokens[0]] + suppliedAmounts[0] <= IERC20(suppliedTokens[0]).balanceOf(address(this)),
                "8"
            );
        } else {
            require(balances[suppliedTokens[0]] + suppliedAmounts[0] <= address(this).balance, "8");
        }
        balances[suppliedTokens[0]] += suppliedAmounts[0];
        return suppliedAmounts[0];
    }

    function burn(
        uint256 tokenId,
        address userAddress,
        uint256 amount,
        address receiver
    ) external override onlyAuthorized returns (address[] memory outTokens, uint256[] memory tokenAmounts) {
        (address lpToken, , ) = decodeId(tokenId);
        PoolInfo storage pool = poolInfo[tokenId];
        pool.userShares[userAddress] -= amount;
        if (lpToken != address(0)) {
            IERC20(lpToken).safeTransfer(receiver, amount);
        } else {
            (bool success,) = payable(receiver).call{ value: amount }("");
            require(success, "Transfer Failed");
        }
        _burn(userAddress, tokenId, amount);
        outTokens = new address[](1);
        tokenAmounts = new uint256[](1);
        outTokens[0] = lpToken;
        tokenAmounts[0] = amount;

        // Sanity check
        if (lpToken != address(0)) {
            require(balances[lpToken] - amount <= IERC20(lpToken).balanceOf(address(this)), "8");
        } else {
            require(balances[lpToken] - amount <= address(this).balance, "8");
        }
        balances[lpToken] -= amount;
    }
}

contracts/Banks/ERC721Bank.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.17;

import "./ERC721Wrappers.sol";
import "./BankBase.sol";

contract ERC721Bank is Initializable, BankBase {
    using Address for address;

    struct PoolInfo {
        address user;
        uint256 liquidity;
        // mapping(address=>mapping(uint=>bool)) userShares;
    }

    address[] nftManagers;
    mapping(address => address) erc721Wrappers;

    mapping(uint256 => PoolInfo) poolInfo;

    function initialize(address _positionsManager) public initializer {
        __BankBase_init(_positionsManager);
    }

    function addManager(address nftManager) external onlyOwner {
        nftManagers.push(nftManager);
    }

    function setWrapper(address nftManager, address wrapper) external onlyOwner {
        erc721Wrappers[nftManager] = wrapper;
    }

    function encodeId(uint256 id, address nftManager) public view returns (uint256) {
        for (uint256 i = 0; i < nftManagers.length; i++) {
            if (nftManagers[i] == nftManager) {
                return (i << 240) | uint240(id);
            }
        }
        revert("NFT manager not supported");
    }

    function decodeId(
        uint256 id
    ) public view override returns (address poolAddress, address nftManager, uint256 pos_id) {
        nftManager = nftManagers[id >> 240];
        pos_id = uint240(id & ((1 << 240) - 1));
        poolAddress = IERC721Wrapper(erc721Wrappers[nftManager]).getPoolAddress(nftManager, pos_id);
    }

    function getLPToken(uint256 id) public view override returns (address managerAddress) {
        (, managerAddress, ) = decodeId(id);
    }

    function getIdFromLpToken(address manager) public view override returns (bool, uint256) {
        for (uint256 i = 0; i < nftManagers.length; i++) {
            if (nftManagers[i] == manager) {
                return (true, uint160(nftManagers[i]));
            }
        }
        return (false, 0);
    }

    function name() public pure override returns (string memory) {
        return "ERC721 Bank";
    }

    function mint(
        uint256 tokenId,
        address userAddress,
        address[] memory suppliedTokens,
        uint256[] memory suppliedAmounts
    ) public override onlyAuthorized returns (uint256) {
        (, , , , , , , uint256 minted, , , , ) = INonfungiblePositionManager(suppliedTokens[0]).positions(
            suppliedAmounts[0]
        );
        poolInfo[tokenId] = PoolInfo(userAddress, minted);
        return minted;
    }

    function mintRecurring(
        uint256 tokenId,
        address userAddress,
        address[] memory suppliedTokens,
        uint256[] memory suppliedAmounts
    ) external override onlyAuthorized returns (uint256) {
        require(poolInfo[tokenId].user == userAddress, "9");
        (, address manager, uint256 id) = decodeId(tokenId);
        address wrapper = erc721Wrappers[manager];

        bytes memory returnData = wrapper.functionDelegateCall(
            abi.encodeWithSelector(IERC721Wrapper.deposit.selector, manager, id, suppliedTokens, suppliedAmounts)
        );
        uint256 minted = abi.decode(returnData, (uint256));
        poolInfo[tokenId].liquidity += minted;
        return minted;
    }

    function burn(
        uint256 tokenId,
        address userAddress,
        uint256 amount,
        address receiver
    ) external override onlyAuthorized returns (address[] memory outTokens, uint256[] memory tokenAmounts) {
        require(poolInfo[tokenId].user == userAddress, "9");
        (, address manager, uint256 id) = decodeId(tokenId);
        address wrapper = erc721Wrappers[manager];
        bytes memory returnData = wrapper.functionDelegateCall(
            abi.encodeWithSelector(IERC721Wrapper.withdraw.selector, manager, id, amount, receiver)
        );
        (outTokens, tokenAmounts) = abi.decode(returnData, (address[], uint256[]));
        // (outTokens, tokenAmounts) = withdraw(manager, id, amount, receiver);
        (, , , , , , , uint256 liquidity, , , , ) = INonfungiblePositionManager(manager).positions(id);
        poolInfo[tokenId].liquidity = liquidity;
    }

    function harvest(
        uint256 tokenId,
        address userAddress,
        address receiver
    ) external override onlyAuthorized returns (address[] memory rewardAddresses, uint256[] memory rewardAmounts) {
        require(poolInfo[tokenId].user == userAddress, "9");
        (, address manager, uint256 id) = decodeId(tokenId);
        address wrapper = erc721Wrappers[manager];
        bytes memory returnData = wrapper.functionDelegateCall(
            abi.encodeWithSelector(IERC721Wrapper.harvest.selector, manager, id, receiver)
        );
        (, , , , , , , uint256 liquidity, , , , ) = INonfungiblePositionManager(manager).positions(id);
        poolInfo[tokenId].liquidity = liquidity;
        (rewardAddresses, rewardAmounts) = abi.decode(returnData, (address[], uint256[]));
        return (rewardAddresses, rewardAmounts);
    }

    function getUnderlyingForFirstDeposit(
        uint256 tokenId
    ) public view override returns (address[] memory underlying, uint256[] memory ratios) {
        (, address manager, ) = decodeId(tokenId);
        underlying = new address[](1);
        underlying[0] = manager;
        ratios = new uint256[](1);
        ratios[0] = 1;
    }

    function getUnderlyingForRecurringDeposit(
        uint256 tokenId
    ) public view override returns (address[] memory, uint256[] memory ratios) {
        (, address manager, uint256 pos_id) = decodeId(tokenId);
        IERC721Wrapper wrapper = IERC721Wrapper(erc721Wrappers[manager]);
        return wrapper.getRatio(manager, pos_id);
        // return wrapper.getERC20Base(pool);
    }

    function getRewards(uint256 tokenId) external view override returns (address[] memory rewardsArray) {
        (rewardsArray, ) = getUnderlyingForRecurringDeposit(tokenId);
    }

    function getPendingRewardsForUser(
        uint256 tokenId,
        address user
    ) external view override returns (address[] memory rewards, uint256[] memory amounts) {
        (, address manager, uint256 pos_id) = decodeId(tokenId);
        IERC721Wrapper wrapper = IERC721Wrapper(erc721Wrappers[manager]);
        return wrapper.getRewardsForPosition(manager, pos_id);
    }

    function getPositionTokens(
        uint256 tokenId,
        address userAddress
    ) external view override returns (address[] memory outTokens, uint256[] memory tokenAmounts) {
        (, address manager, uint256 pos_id) = decodeId(tokenId);
        IERC721Wrapper wrapper = IERC721Wrapper(erc721Wrappers[manager]);
        return wrapper.getPositionUnderlying(manager, pos_id);
    }

    function isUnderlyingERC721() external pure override returns (bool) {
        return true;
    }
}

contracts/Banks/ERC721Wrappers.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.17;

import "../interfaces/UniswapV3/INonfungiblePositionManager.sol";
import "../interfaces/UniswapV3/IUniswapV3Pool.sol";
import "../interfaces/UniswapV3/IUniswapV3Factory.sol";
import "./BankBase.sol";
import "../libraries/TickMath.sol";
import "../libraries/LiquidityAmounts.sol";
import "hardhat/console.sol";

abstract contract IERC721Wrapper is Ownable {
    function isSupported(address manager, address pool) external view virtual returns (bool);

    function getPoolAddress(address manager, uint256 id) external view virtual returns (address);

    function deposit(
        address manager,
        uint256 id,
        address[] memory suppliedTokens,
        uint256[] memory suppliedAmounts
    ) external virtual returns (uint256);

    function withdraw(
        address manager,
        uint256 id,
        uint256 amount,
        address receiver
    ) external virtual returns (address[] memory outTokens, uint256[] memory tokenAmounts);

    function harvest(
        address manager,
        uint256 id,
        address receiver
    ) external virtual returns (address[] memory outTokens, uint256[] memory tokenAmounts);

    function getRatio(
        address manager,
        uint256 id
    ) external view virtual returns (address[] memory tokens, uint256[] memory ratios);

    function getRewardsForPosition(
        address manager,
        uint256 tokenId
    ) external view virtual returns (address[] memory rewards, uint256[] memory amounts);

    function getERC20Base(address pool) external view virtual returns (address[] memory underlyingTokens);

    function getPositionUnderlying(
        address manager,
        uint256 tokenId
    ) external view virtual returns (address[] memory tokens, uint256[] memory amounts);
}

contract UniswapV3Wrapper is IERC721Wrapper {
    using SaferERC20 for IERC20;

    function isSupported(address managerAddress, address poolAddress) external view override returns (bool) {
        IUniswapV3Pool pool = IUniswapV3Pool(poolAddress);
        address token0 = pool.token0();
        address token1 = pool.token1();
        uint24 fee = pool.fee();
        INonfungiblePositionManager manager = INonfungiblePositionManager(managerAddress);
        IUniswapV3Factory factory = IUniswapV3Factory(manager.factory());
        address expectedPoolAddress = factory.getPool(token0, token1, fee);
        if (expectedPoolAddress == poolAddress) {
            return true;
        }
        return false;
    }

    function getPoolAddress(address manager, uint256 id) external view override returns (address) {
        (, , address token0, address token1, uint24 fee, , , , , , , ) = INonfungiblePositionManager(manager).positions(
            id
        );
        address factory = INonfungiblePositionManager(manager).factory();
        address poolAddress = IUniswapV3Factory(factory).getPool(token0, token1, fee);
        return poolAddress;
    }

    function deposit(
        address manager,
        uint256 id,
        address[] memory suppliedTokens,
        uint256[] memory suppliedAmounts
    ) external override returns (uint256) {
        for (uint256 i = 0; i < suppliedTokens.length; i++) {
            IERC20(suppliedTokens[i]).safeIncreaseAllowance(manager, suppliedAmounts[i]);
        }
        (, , address token0, address token1, , , , , , , , ) = INonfungiblePositionManager(manager).positions(id);
        uint256 amount0;
        uint256 amount1;
        if (token0 == suppliedTokens[0] && token1 == suppliedTokens[1]) {
            amount0 = suppliedAmounts[0];
            amount1 = suppliedAmounts[1];
        } else {
            amount1 = suppliedAmounts[0];
            amount0 = suppliedAmounts[1];
        }
        INonfungiblePositionManager.IncreaseLiquidityParams memory params = INonfungiblePositionManager
            .IncreaseLiquidityParams(id, amount0, amount1, 0, 0, block.timestamp);
        (uint256 minted, uint256 a0, uint256 a1) = INonfungiblePositionManager(manager).increaseLiquidity(params);
        address owner = INonfungiblePositionManager(manager).ownerOf(id);
        // Refund left overs
        if (token0 == suppliedTokens[0] && token1 == suppliedTokens[1]) {
            IERC20(token0).safeTransfer(owner, suppliedAmounts[0] - a0);
            IERC20(token1).safeTransfer(owner, suppliedAmounts[1] - a1);
        } else {
            IERC20(token0).safeTransfer(owner, suppliedAmounts[1] - a0);
            IERC20(token1).safeTransfer(owner, suppliedAmounts[0] - a1);
        }
        return minted;
    }

    function withdraw(
        address manager,
        uint256 id,
        uint256 amount,
        address receiver
    ) external override returns (address[] memory outTokens, uint256[] memory tokenAmounts) {
        (, , address token0, address token1, , , , , , , , ) = INonfungiblePositionManager(manager).positions(id);
        INonfungiblePositionManager.DecreaseLiquidityParams memory withdrawParams = INonfungiblePositionManager
            .DecreaseLiquidityParams(id, uint128(amount), 0, 0, block.timestamp);
        (uint256 token0Amount, uint256 token1Amount) = INonfungiblePositionManager(manager).decreaseLiquidity(
            withdrawParams
        );
        INonfungiblePositionManager.CollectParams memory params = INonfungiblePositionManager.CollectParams(
            id,
            address(this),
            2 ** 128 - 1,
            2 ** 128 - 1
        );
        INonfungiblePositionManager(manager).collect(params);
        IERC20(token0).safeTransfer(receiver, token0Amount);
        IERC20(token1).safeTransfer(receiver, token1Amount);
        outTokens = new address[](2);
        outTokens[0] = token0;
        outTokens[1] = token1;
        tokenAmounts = new uint256[](2);
        tokenAmounts[0] = token0Amount;
        tokenAmounts[1] = token1Amount;
    }

    function harvest(
        address manager,
        uint256 id,
        address receiver
    ) external override returns (address[] memory outTokens, uint256[] memory tokenAmounts) {
        (, , address token0, address token1, , , , , , , , ) = INonfungiblePositionManager(manager).positions(id);
        INonfungiblePositionManager.CollectParams memory params = INonfungiblePositionManager.CollectParams(
            id,
            receiver,
            2 ** 128 - 1,
            2 ** 128 - 1
        );
        (uint256 amount0, uint256 amount1) = INonfungiblePositionManager(manager).collect(params);
        outTokens = new address[](2);
        outTokens[0] = token0;
        outTokens[1] = token1;
        tokenAmounts = new uint256[](2);
        tokenAmounts[0] = amount0;
        tokenAmounts[1] = amount1;
    }

    function getRatio(
        address manager,
        uint256 tokenId
    ) external view override returns (address[] memory tokens, uint256[] memory ratios) {
        (
            ,
            ,
            address token0,
            address token1,
            uint24 fee,
            int24 tick0,
            int24 tick1,
            ,
            ,
            ,
            ,

        ) = INonfungiblePositionManager(manager).positions(tokenId);
        IUniswapV3Factory factory = IUniswapV3Factory(INonfungiblePositionManager(manager).factory());
        IUniswapV3Pool pool = IUniswapV3Pool(factory.getPool(token0, token1, fee));
        int24 currentTick;
        (uint160 sqrtPriceX96, , , , , , ) = pool.slot0();
        {
            currentTick = TickMath.getTickAtSqrtRatio(sqrtPriceX96);
            uint absTick = currentTick < 0 ? uint(-int(currentTick)) : uint(int(currentTick));
            uint24 tickSpacing = uint24(pool.tickSpacing());
            absTick -= absTick % tickSpacing;
            currentTick = currentTick < 0 ? -int24(int(absTick)) : int24(int(absTick));
            tokens = new address[](2);
            tokens[0] = token0;
            tokens[1] = token1;
        }
        ratios = new uint256[](2);
        {
            (uint256 amount0, uint256 amount1) = LiquidityAmounts.getAmountsForLiquidity(
                TickMath.getSqrtRatioAtTick(currentTick),
                TickMath.getSqrtRatioAtTick(tick0),
                TickMath.getSqrtRatioAtTick(tick1),
                pool.liquidity()
            );
            uint256 price;
            uint256 MAX = 2 ** 256 - 1;
            if (uint256(sqrtPriceX96) * uint256(sqrtPriceX96) > MAX / 1e18) {
                price = ((uint256(sqrtPriceX96) * uint256(sqrtPriceX96)) >> (96 * 2)) * 1e18;
            } else {
                price = (uint256(sqrtPriceX96) * uint256(sqrtPriceX96) * 1e18) >> (96 * 2);
            }
            ratios[0] = amount0;
            ratios[1] = (amount1 * 1e18) / price;
        }
    }

    function getRewardsForPosition(
        address manager,
        uint256 tokenId
    ) external view override returns (address[] memory rewards, uint256[] memory amounts) {
        (uint256 fee0, uint256 fee1) = getPendingFees(manager, tokenId);
        (, , address token0, address token1, , , , , , , , ) = INonfungiblePositionManager(manager).positions(tokenId);
        rewards = new address[](2);
        rewards[0] = token0;
        rewards[1] = token1;
        amounts = new uint256[](2);
        amounts[0] = fee0;
        amounts[1] = fee1;
    }

    function getPositionUnderlying(
        address manager,
        uint256 tokenId
    ) external view override returns (address[] memory tokens, uint256[] memory amounts) {
        (
            ,
            ,
            address token0,
            address token1,
            uint24 fee,
            int24 tickLower,
            int24 tickUpper,
            uint128 liquidity,
            ,
            ,
            ,

        ) = INonfungiblePositionManager(manager).positions(tokenId);
        IUniswapV3Pool pool = IUniswapV3Pool(
            IUniswapV3Factory(INonfungiblePositionManager(manager).factory()).getPool(token0, token1, fee)
        );
        (uint160 sqrtRatioX96, , , , , , ) = pool.slot0();
        (uint256 amount0, uint256 amount1) = LiquidityAmounts.getAmountsForLiquidity(
            sqrtRatioX96,
            TickMath.getSqrtRatioAtTick(tickLower),
            TickMath.getSqrtRatioAtTick(tickUpper),
            liquidity
        );
        tokens = new address[](2);
        tokens[0] = token0;
        tokens[1] = token1;
        amounts = new uint256[](2);
        amounts[0] = amount0;
        amounts[1] = amount1;
    }

    function getERC20Base(address poolAddress) external view override returns (address[] memory underlyingTokens) {
        IUniswapV3Pool pool = IUniswapV3Pool(poolAddress);
        address token0 = pool.token0();
        address token1 = pool.token1();
        underlyingTokens = new address[](2);
        underlyingTokens[0] = token0;
        underlyingTokens[1] = token1;
    }

    function getPendingFees(
        address manager,
        uint256 _positionId
    ) internal view returns (uint256 feeAmt0, uint256 feeAmt1) {
        int24 tickLower;
        int24 tickUpper;
        uint256 feeGrowthInside0LastX128;
        uint256 feeGrowthInside1LastX128;
        IUniswapV3Pool pool;
        {
            address token0;
            address token1;
            uint24 fee;
            (
                ,
                ,
                token0,
                token1,
                fee,
                tickLower,
                tickUpper,
                ,
                feeGrowthInside0LastX128,
                feeGrowthInside1LastX128,
                ,

            ) = INonfungiblePositionManager(manager).positions(_positionId);
            pool = IUniswapV3Pool(
                IUniswapV3Factory(INonfungiblePositionManager(manager).factory()).getPool(token0, token1, fee)
            );
        }
        (, int24 curTick, , , , , ) = pool.slot0();

        (uint128 liquidity, , , uint128 tokensOwed0, uint128 tokensOwed1) = pool.positions(
            _getPositionID(manager, tickLower, tickUpper)
        );

        feeAmt0 =
            _computeFeesEarned(pool, true, feeGrowthInside0LastX128, curTick, tickLower, tickUpper, liquidity) +
            tokensOwed0;
        feeAmt1 =
            _computeFeesEarned(pool, false, feeGrowthInside1LastX128, curTick, tickLower, tickUpper, liquidity) +
            tokensOwed1;
    }

    function _getPositionID(
        address _owner,
        int24 _lowerTick,
        int24 _upperTick
    ) internal pure returns (bytes32 positionId) {
        return keccak256(abi.encodePacked(_owner, _lowerTick, _upperTick));
    }

    // ref: from arrakis finance: https://github.com/ArrakisFinance/vault-v1-core/blob/main/contracts/ArrakisVaultV1.sol
    function _computeFeesEarned(
        IUniswapV3Pool _pool,
        bool _isZero,
        uint256 _feeGrowthInsideLast,
        int24 _tick,
        int24 _lowerTick,
        int24 _upperTick,
        uint128 _liquidity
    ) internal view returns (uint256 fee) {
        uint256 feeGrowthOutsideLower;
        uint256 feeGrowthOutsideUpper;
        uint256 feeGrowthGlobal;
        if (_isZero) {
            feeGrowthGlobal = _pool.feeGrowthGlobal0X128();
            (, , feeGrowthOutsideLower, , , , , ) = _pool.ticks(_lowerTick);
            (, , feeGrowthOutsideUpper, , , , , ) = _pool.ticks(_upperTick);
        } else {
            feeGrowthGlobal = _pool.feeGrowthGlobal1X128();
            (, , , feeGrowthOutsideLower, , , , ) = _pool.ticks(_lowerTick);
            (, , , feeGrowthOutsideUpper, , , , ) = _pool.ticks(_upperTick);
        }

        unchecked {
            // calculate fee growth below
            uint256 feeGrowthBelow;
            if (_tick >= _lowerTick) {
                feeGrowthBelow = feeGrowthOutsideLower;
            } else {
                feeGrowthBelow = feeGrowthGlobal - feeGrowthOutsideLower;
            }

            // calculate fee growth above
            uint256 feeGrowthAbove;
            if (_tick < _upperTick) {
                feeGrowthAbove = feeGrowthOutsideUpper;
            } else {
                feeGrowthAbove = feeGrowthGlobal - feeGrowthOutsideUpper;
            }

            uint256 feeGrowthInside = feeGrowthGlobal - feeGrowthBelow - feeGrowthAbove;
            fee = (_liquidity * (feeGrowthInside - _feeGrowthInsideLast)) / 2 ** 128;
        }
    }
}

contracts/Banks/MasterChefBank.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.17;

import "./BankBase.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "./MasterChefWrappers.sol";
import "hardhat/console.sol";

contract MasterChefBank is Initializable, ERC1155Upgradeable, BankBase {
    using Address for address;
    using SaferERC20 for IERC20;

    event SetMasterChefWrapper(address masterChef, address wrapper);

    struct PoolInfo {
        address lpToken;
        uint256 lpSupply;
        mapping(address => uint256) rewardAllocationsPerShare;
        mapping(address => uint256) userShares;
        mapping(address => mapping(address => int256)) rewardDebt; // Mapping from user to reward to debt
    }

    uint256 PRECISION;
    mapping(uint256 => PoolInfo) public poolInfo;
    mapping(address => address) public masterChefWrappers;
    address[] public supportedMasterChefs;
    mapping(address => uint256) public balances;

    function initialize(address _positionsManager) public initializer {
        PRECISION = 1e12;
        __BankBase_init(_positionsManager);
        __ERC1155_init("MasterChefBank");
    }

    function encodeId(address masterChef, uint256 pid) public pure returns (uint256) {
        return (pid << 160) | uint160(masterChef);
    }

    function setMasterChefWrapper(address masterChef, address wrapper) external onlyOwner {
        masterChefWrappers[masterChef] = wrapper;
        for (uint256 i = 0; i < supportedMasterChefs.length; i++) {
            if (supportedMasterChefs[i] == masterChef) {
                return;
            }
        }
        supportedMasterChefs.push(masterChef);
        emit SetMasterChefWrapper(masterChef, wrapper);
    }

    function decodeId(uint256 id) public view override returns (address lpToken, address masterChef, uint256 pid) {
        pid = id >> 160;
        masterChef = address(uint160(id & ((1 << 160) - 1)));
        lpToken = IMasterChefWrapper(masterChefWrappers[masterChef]).getLpToken(pid);
    }

    function getLPToken(uint256 id) public view override returns (address tokenAddress) {
        (tokenAddress, , ) = decodeId(id);
    }

    function name() public pure override returns (string memory) {
        return "Masterchef Bank";
    }

    function getIdFromLpToken(address lpToken) public view override returns (bool, uint256) {
        for (uint256 i = 0; i < supportedMasterChefs.length; i++) {
            IMasterChefWrapper wrapper = IMasterChefWrapper(masterChefWrappers[supportedMasterChefs[i]]);
            (bool success, uint256 id) = wrapper.getIdFromLpToken(lpToken);
            if (success) {
                return (true, encodeId(supportedMasterChefs[i], id));
            }
        }
        return (false, 0);
    }

    function getRewards(uint256 tokenId) external view override returns (address[] memory) {
        (, address masterChef, uint256 pid) = decodeId(tokenId);
        IMasterChefWrapper wrapper = IMasterChefWrapper(masterChefWrappers[masterChef]);
        return wrapper.getRewards(pid);
    }

    function getPendingRewardsForUser(
        uint256 tokenId,
        address user
    ) external view override returns (address[] memory rewards, uint256[] memory amounts) {
        PoolInfo storage pool = poolInfo[tokenId];
        uint256 lpSupply = pool.lpSupply;
        (, address masterChef, uint256 pid) = decodeId(tokenId);
        uint256[] memory everyonesRewardAmounts;
        (rewards, everyonesRewardAmounts) = IMasterChefWrapper(masterChefWrappers[masterChef]).getPendingRewards(pid);
        amounts = new uint256[](rewards.length);
        if (lpSupply > 0) {
            for (uint256 i = 0; i < rewards.length; i++) {
                address reward = rewards[i];
                uint256 allocationPerShare = pool.rewardAllocationsPerShare[rewards[i]] +
                    (everyonesRewardAmounts[i] * PRECISION) /
                    lpSupply;
                int256 accumulatedReward = int256((pool.userShares[user] * allocationPerShare) / PRECISION);
                uint256 pendingReward = uint256(accumulatedReward - pool.rewardDebt[user][reward]);
                amounts[i] = pendingReward;
            }
        }
    }

    function getPositionTokens(
        uint256 tokenId,
        address userAddress
    ) external view override returns (address[] memory outTokens, uint256[] memory tokenAmounts) {
        (address lpToken, , ) = decodeId(tokenId);
        uint256 amount = balanceOf(userAddress, tokenId);
        outTokens = new address[](1);
        tokenAmounts = new uint256[](1);
        outTokens[0] = lpToken;
        tokenAmounts[0] = amount;
    }

    function _harvest(address masterChef, address lpToken, uint256 pid) internal {
        IERC20(lpToken).safeIncreaseAllowance(masterChef, 10);
        address masterChefWrapperAddress = masterChefWrappers[masterChef];
        IMasterChefWrapper wrapper = IMasterChefWrapper(masterChefWrappers[masterChef]);
        masterChefWrapperAddress.functionDelegateCall(
            abi.encodeWithSelector(wrapper.harvest.selector, masterChef, pid)
        );
    }

    function updateToken(uint256 tokenId) internal onlyAuthorized {
        (address lpToken, address masterChef, uint256 pid) = decodeId(tokenId);
        PoolInfo storage pool = poolInfo[tokenId];
        uint256 lpSupply = pool.lpSupply;
        if (lpSupply > 0) {
            address[] memory rewards = IMasterChefWrapper(masterChefWrappers[masterChef]).getRewards(pid);
            uint256[] memory rewardAmounts = new uint256[](rewards.length);
            for (uint256 i = 0; i < rewards.length; i++) {
                rewardAmounts[i] = IERC20(rewards[i]).balanceOf(address(this));
            }
            _harvest(masterChef, lpToken, pid);
            for (uint256 i = 0; i < rewards.length; i++) {
                rewardAmounts[i] = IERC20(rewards[i]).balanceOf(address(this)) - rewardAmounts[i];
                pool.rewardAllocationsPerShare[rewards[i]] += (rewardAmounts[i] * PRECISION) / lpSupply;
            }
        }
    }

    function _deposit(address masterChef, uint256 pid, uint256 amount) internal {
        address masterChefWrapper = masterChefWrappers[masterChef];
        masterChefWrapper.functionDelegateCall(
            abi.encodeWithSelector(IMasterChefWrapper.deposit.selector, masterChef, pid, amount)
        );
    }

    function _withdraw(address masterChef, uint256 pid, uint256 amount) internal {
        address masterChefWrapper = masterChefWrappers[masterChef];
        masterChefWrapper.functionDelegateCall(
            abi.encodeWithSelector(IMasterChefWrapper.withdraw.selector, masterChef, pid, amount)
        );
    }

    function mint(
        uint256 tokenId,
        address userAddress,
        address[] memory suppliedTokens,
        uint256[] memory suppliedAmounts
    ) public override onlyAuthorized returns (uint256) {
        updateToken(tokenId);
        (address lpToken, address masterChef, uint256 pid) = decodeId(tokenId);
        require(lpToken == suppliedTokens[0], "6");
        IERC20(lpToken).safeIncreaseAllowance(masterChef, suppliedAmounts[0]);
        _deposit(masterChef, pid, suppliedAmounts[0]);
        PoolInfo storage pool = poolInfo[tokenId];
        pool.userShares[userAddress] += suppliedAmounts[0];
        pool.lpSupply += suppliedAmounts[0];
        address[] memory rewards = IMasterChefWrapper(masterChefWrappers[masterChef]).getRewards(pid);
        for (uint256 i = 0; i < rewards.length; i++) {
            address reward = rewards[i];
            pool.rewardDebt[userAddress][reward] += int256(
                (suppliedAmounts[0] * pool.rewardAllocationsPerShare[reward]) / PRECISION
            );
        }
        _mint(userAddress, tokenId, suppliedAmounts[0], "");
        emit Mint(tokenId, userAddress, suppliedAmounts[0]);
        return suppliedAmounts[0];
    }

    function burn(
        uint256 tokenId,
        address userAddress,
        uint256 amount,
        address receiver
    ) external override onlyAuthorized returns (address[] memory outTokens, uint256[] memory tokenAmounts) {
        updateToken(tokenId);
        (address lpToken, address masterChef, uint256 pid) = decodeId(tokenId);
        PoolInfo storage pool = poolInfo[tokenId];
        address[] memory rewards = IMasterChefWrapper(masterChefWrappers[masterChef]).getRewards(pid);
        for (uint256 i = 0; i < rewards.length; i++) {
            address reward = rewards[i];
            pool.rewardDebt[userAddress][reward] -= int256(
                (amount * pool.rewardAllocationsPerShare[reward]) / PRECISION
            );
        }
        pool.userShares[userAddress] -= amount;
        pool.lpSupply -= amount;
        _withdraw(masterChef, pid, amount);
        IERC20(lpToken).safeTransfer(receiver, amount);
        _burn(userAddress, tokenId, amount);
        emit Burn(tokenId, userAddress, amount, receiver);
        outTokens = new address[](1);
        tokenAmounts = new uint256[](1);
        outTokens[0] = lpToken;
        tokenAmounts[0] = amount;
    }

    function harvest(
        uint256 tokenId,
        address userAddress,
        address receiver
    ) external override onlyAuthorized returns (address[] memory rewardAddresses, uint256[] memory rewardAmounts) {
        updateToken(tokenId);
        PoolInfo storage pool = poolInfo[tokenId];
        (, address masterChef, uint256 pid) = decodeId(tokenId);
        address[] memory rewards = IMasterChefWrapper(masterChefWrappers[masterChef]).getRewards(pid);
        rewardAddresses = new address[](rewards.length);
        rewardAmounts = new uint256[](rewards.length);
        for (uint256 i = 0; i < rewards.length; i++) {
            address reward = rewards[i];
            int256 accumulatedReward = int256(
                (pool.userShares[userAddress] * pool.rewardAllocationsPerShare[reward]) / PRECISION
            );
            uint256 pendingReward = uint256(accumulatedReward - pool.rewardDebt[userAddress][reward]);
            pool.rewardDebt[userAddress][reward] = accumulatedReward;
            if (pendingReward != 0) {
                IERC20(reward).safeTransfer(receiver, pendingReward);
            }
            rewardAddresses[i] = rewards[i];
            rewardAmounts[i] = pendingReward;
        }
        emit Harvest(tokenId, userAddress, receiver);
    }
}

contracts/Banks/MasterChefWrappers.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.17;

import "./BankBase.sol";
import "../interfaces/MasterChefInterfaces.sol";
import "../libraries/AddressArray.sol";
import "../libraries/UintArray.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "hardhat/console.sol";

abstract contract IMasterChefWrapper is Ownable {
    using AddressArray for address[];
    using UintArray for uint256[];
    using Address for address;

    event LPTokenAdded(address masterChef, address lpToken, uint256 poolId);

    mapping(address => bool) public supportedLps;
    mapping(address => uint256) public supportedLpIndices;
    address public masterChef;
    address public baseReward;
    string public pendingRewardGetter;

    function getRewards(uint256 pid) external view virtual returns (address[] memory) {
        address[] memory rewards = new address[](1);
        rewards[0] = baseReward;
        return rewards;
    }

    function initialize() public virtual {
        uint256 poolLength = IMasterChefV1(masterChef).poolLength();
        for (uint256 i = 0; i < poolLength; i++) {
            setSupported(i);
        }
    }

    function setSupported(uint256 pid) public virtual {
        address lpToken = getLpToken(pid);
        supportedLpIndices[lpToken] = pid;
        supportedLps[lpToken] = true;
    }

    function getIdFromLpToken(address lpToken) external view virtual returns (bool, uint256) {
        if (!supportedLps[lpToken] || lpToken == baseReward) return (false, 0);
        else return (true, supportedLpIndices[lpToken]);
    }

    function getPendingRewards(
        uint256 pid
    ) external view virtual returns (address[] memory rewards, uint256[] memory amounts) {
        bytes memory returnData = masterChef.functionStaticCall(
            abi.encodeWithSignature(pendingRewardGetter, pid, msg.sender)
        );
        uint256 pending = abi.decode(returnData, (uint256));
        rewards = new address[](1);
        rewards[0] = baseReward;
        amounts = new uint256[](1);
        amounts[0] = pending;
    }

    function getLpToken(uint256 pid) public view virtual returns (address);

    function deposit(address masterChef, uint256 pid, uint256 amount) external virtual;

    function withdraw(address masterChef, uint256 pid, uint256 amount) external virtual;

    function harvest(address masterChef, uint256 pid) external virtual;
}

contract MasterChefV1Wrapper is IMasterChefWrapper {
    using AddressArray for address[];
    using UintArray for uint256[];
    using Address for address;

    constructor(address _masterChef, address _baseReward, string memory _pendingRewardGetter) {
        masterChef = _masterChef;
        baseReward = _baseReward;
        pendingRewardGetter = _pendingRewardGetter;
        initialize();
    }

    // function getIdFromLpToken(address lpToken) override external view returns (bool, uint) {
    //     uint poolLength = IMasterChefV1(masterChef).poolLength();
    //     for (uint i = 0; i<poolLength; i++) {
    //         IMasterChefV1.PoolInfo memory poolInfo = IMasterChefV1(masterChef).poolInfo(i);
    //         if (poolInfo.lpToken==lpToken) {
    //             return (true, i);
    //         }
    //     }
    //     return (false, 0);
    // }

    function getLpToken(uint256 pid) public view override returns (address) {
        IMasterChefV1.PoolInfo memory pool = IMasterChefV1(masterChef).poolInfo(pid);
        return pool.lpToken;
    }

    function deposit(address masterChef, uint256 pid, uint256 amount) external override {
        IMasterChefV1(masterChef).deposit(pid, amount);
    }

    function withdraw(address masterChef, uint256 pid, uint256 amount) external override {
        IMasterChefV1(masterChef).withdraw(pid, amount);
    }

    function harvest(address masterChef, uint256 pid) external override {
        IMasterChefV1(masterChef).withdraw(pid, 10);
        IMasterChefV1(masterChef).deposit(pid, 10);
    }
}

contract MasterChefV2Wrapper is IMasterChefWrapper {
    using AddressArray for address[];
    using UintArray for uint256[];
    using Address for address;
    mapping(uint256 => address) extraRewards;

    constructor(address _masterChef, address _baseReward, string memory _pendingRewardGetter) {
        masterChef = _masterChef;
        baseReward = _baseReward;
        pendingRewardGetter = _pendingRewardGetter;
        initialize();
    }

    // function getIdFromLpToken(address lpToken) override external view returns (bool, uint) {
    //     uint poolLength = ISushiSwapMasterChefV2(masterChef).poolLength();
    //     for (uint i = 0; i<poolLength; i++) {
    //         if (ISushiSwapMasterChefV2(masterChef).lpToken(i)==lpToken) {
    //             return (true, i);
    //         }
    //     }
    //     return (false, 0);
    // }

    function getLpToken(uint256 pid) public view override returns (address) {
        return ISushiSwapMasterChefV2(masterChef).lpToken(pid);
    }

    function getRewards(uint256 pid) external view override returns (address[] memory) {
        address[] memory rewards = new address[](1);
        rewards[0] = baseReward;
        address rewarder = ISushiSwapMasterChefV2(masterChef).rewarder(pid);
        if (rewarder != address(0)) {
            (address[] memory tokens, ) = IRewarder(rewarder).pendingTokens(0, address(this), 0);
            rewards = rewards.concat(tokens);
        }
        return rewards;
    }

    function getPendingRewards(
        uint256 pid
    ) external view override returns (address[] memory rewards, uint256[] memory rewardAmounts) {
        bytes memory returnData = masterChef.functionStaticCall(
            abi.encodeWithSignature(pendingRewardGetter, pid, msg.sender)
        );
        uint256 pending = abi.decode(returnData, (uint256));
        rewards = new address[](1);
        rewards[0] = baseReward;
        rewardAmounts = new uint256[](1);
        rewardAmounts[0] = pending;
        address rewarder = ISushiSwapMasterChefV2(masterChef).rewarder(pid);
        if (rewarder != address(0)) {
            (address[] memory tokens, uint256[] memory amounts) = IRewarder(rewarder).pendingTokens(pid, msg.sender, 0);
            rewards = rewards.concat(tokens);
            rewardAmounts = rewardAmounts.concat(amounts);
        }
    }

    function deposit(address masterChef, uint256 pid, uint256 amount) external override {
        ISushiSwapMasterChefV2(masterChef).deposit(pid, amount, address(this));
    }

    function withdraw(address masterChef, uint256 pid, uint256 amount) external override {
        ISushiSwapMasterChefV2(masterChef).withdraw(pid, amount, address(this));
    }

    function harvest(address masterChef, uint256 pid) external override {
        try ISushiSwapMasterChefV2(masterChef).pendingSushi(pid, address(this)) returns (uint256) {
            ISushiSwapMasterChefV2(masterChef).harvest(pid, address(this));
        } catch {}
    }
}

contract PancakeSwapMasterChefV2Wrapper is IMasterChefWrapper {
    using AddressArray for address[];
    using UintArray for uint256[];
    using Address for address;

    constructor(address _masterChef, address _baseReward, string memory _pendingRewardGetter) {
        masterChef = _masterChef;
        baseReward = _baseReward;
        pendingRewardGetter = _pendingRewardGetter;
        initialize();
    }

    // function getIdFromLpToken(address lpToken) override external view returns (bool, uint) {
    //     uint poolLength = IPancakeSwapMasterChefV2(masterChef).poolLength();
    //     for (uint i = 0; i<poolLength; i++) {
    //         if (IPancakeSwapMasterChefV2(masterChef).lpToken(i)==lpToken) {
    //             return (true, i);
    //         }
    //     }
    //     return (false, 0);
    // }

    function getLpToken(uint256 pid) public view override returns (address) {
        return ISushiSwapMasterChefV2(masterChef).lpToken(pid);
    }

    function deposit(address masterChef, uint256 pid, uint256 amount) external override {
        IMasterChefV1(masterChef).deposit(pid, amount);
    }

    function withdraw(address masterChef, uint256 pid, uint256 amount) external override {
        IMasterChefV1(masterChef).withdraw(pid, amount);
    }

    function harvest(address masterChef, uint256 pid) external override {
        IMasterChefV1(masterChef).withdraw(pid, 10);
        IMasterChefV1(masterChef).deposit(pid, 10);
    }
}

