// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity >=0.5.0;

import '@uniswap/v3-core/contracts/libraries/FullMath.sol';
import '@uniswap/v3-core/contracts/libraries/FixedPoint96.sol';

/// @title Liquidity amount functions
/// @notice Provides functions for computing liquidity amounts from token amounts and prices
library LiquidityAmounts {
    /// @notice Downcasts uint256 to uint128
    /// @param x The uint258 to be downcasted
    /// @return y The passed value, downcasted to uint128
    function toUint128(uint256 x) private pure returns (uint128 y) {
        require((y = uint128(x)) == x);
    }

    /// @notice Computes the amount of liquidity received for a given amount of token0 and price range
    /// @dev Calculates amount0 * (sqrt(upper) * sqrt(lower)) / (sqrt(upper) - sqrt(lower))
    /// @param sqrtRatioAX96 A sqrt price representing the first tick boundary
    /// @param sqrtRatioBX96 A sqrt price representing the second tick boundary
    /// @param amount0 The amount0 being sent in
    /// @return liquidity The amount of returned liquidity
    function getLiquidityForAmount0(
        uint160 sqrtRatioAX96,
        uint160 sqrtRatioBX96,
        uint256 amount0
    ) internal pure returns (uint128 liquidity) {
        if (sqrtRatioAX96 > sqrtRatioBX96) (sqrtRatioAX96, sqrtRatioBX96) = (sqrtRatioBX96, sqrtRatioAX96);
        uint256 intermediate = FullMath.mulDiv(sqrtRatioAX96, sqrtRatioBX96, FixedPoint96.Q96);
        return toUint128(FullMath.mulDiv(amount0, intermediate, sqrtRatioBX96 - sqrtRatioAX96));
    }

    /// @notice Computes the amount of liquidity received for a given amount of token1 and price range
    /// @dev Calculates amount1 / (sqrt(upper) - sqrt(lower)).
    /// @param sqrtRatioAX96 A sqrt price representing the first tick boundary
    /// @param sqrtRatioBX96 A sqrt price representing the second tick boundary
    /// @param amount1 The amount1 being sent in
    /// @return liquidity The amount of returned liquidity
    function getLiquidityForAmount1(
        uint160 sqrtRatioAX96,
        uint160 sqrtRatioBX96,
        uint256 amount1
    ) internal pure returns (uint128 liquidity) {
        if (sqrtRatioAX96 > sqrtRatioBX96) (sqrtRatioAX96, sqrtRatioBX96) = (sqrtRatioBX96, sqrtRatioAX96);
        return toUint128(FullMath.mulDiv(amount1, FixedPoint96.Q96, sqrtRatioBX96 - sqrtRatioAX96));
    }

    /// @notice Computes the maximum amount of liquidity received for a given amount of token0, token1, the current
    /// pool prices and the prices at the tick boundaries
    /// @param sqrtRatioX96 A sqrt price representing the current pool prices
    /// @param sqrtRatioAX96 A sqrt price representing the first tick boundary
    /// @param sqrtRatioBX96 A sqrt price representing the second tick boundary
    /// @param amount0 The amount of token0 being sent in
    /// @param amount1 The amount of token1 being sent in
    /// @return liquidity The maximum amount of liquidity received
    function getLiquidityForAmounts(
        uint160 sqrtRatioX96,
        uint160 sqrtRatioAX96,
        uint160 sqrtRatioBX96,
        uint256 amount0,
        uint256 amount1
    ) internal pure returns (uint128 liquidity) {
        if (sqrtRatioAX96 > sqrtRatioBX96) (sqrtRatioAX96, sqrtRatioBX96) = (sqrtRatioBX96, sqrtRatioAX96);

        if (sqrtRatioX96 <= sqrtRatioAX96) {
            liquidity = getLiquidityForAmount0(sqrtRatioAX96, sqrtRatioBX96, amount0);
        } else if (sqrtRatioX96 < sqrtRatioBX96) {
            uint128 liquidity0 = getLiquidityForAmount0(sqrtRatioX96, sqrtRatioBX96, amount0);
            uint128 liquidity1 = getLiquidityForAmount1(sqrtRatioAX96, sqrtRatioX96, amount1);

            liquidity = liquidity0 < liquidity1 ? liquidity0 : liquidity1;
        } else {
            liquidity = getLiquidityForAmount1(sqrtRatioAX96, sqrtRatioBX96, amount1);
        }
    }

    /// @notice Computes the amount of token0 for a given amount of liquidity and a price range
    /// @param sqrtRatioAX96 A sqrt price representing the first tick boundary
    /// @param sqrtRatioBX96 A sqrt price representing the second tick boundary
    /// @param liquidity The liquidity being valued
    /// @return amount0 The amount of token0
    function getAmount0ForLiquidity(
        uint160 sqrtRatioAX96,
        uint160 sqrtRatioBX96,
        uint128 liquidity
    ) internal pure returns (uint256 amount0) {
        if (sqrtRatioAX96 > sqrtRatioBX96) (sqrtRatioAX96, sqrtRatioBX96) = (sqrtRatioBX96, sqrtRatioAX96);

        return
            FullMath.mulDiv(
                uint256(liquidity) << FixedPoint96.RESOLUTION,
                sqrtRatioBX96 - sqrtRatioAX96,
                sqrtRatioBX96
            ) / sqrtRatioAX96;
    }

    /// @notice Computes the amount of token1 for a given amount of liquidity and a price range
    /// @param sqrtRatioAX96 A sqrt price representing the first tick boundary
    /// @param sqrtRatioBX96 A sqrt price representing the second tick boundary
    /// @param liquidity The liquidity being valued
    /// @return amount1 The amount of token1
    function getAmount1ForLiquidity(
        uint160 sqrtRatioAX96,
        uint160 sqrtRatioBX96,
        uint128 liquidity
    ) internal pure returns (uint256 amount1) {
        if (sqrtRatioAX96 > sqrtRatioBX96) (sqrtRatioAX96, sqrtRatioBX96) = (sqrtRatioBX96, sqrtRatioAX96);

        return FullMath.mulDiv(liquidity, sqrtRatioBX96 - sqrtRatioAX96, FixedPoint96.Q96);
    }

    /// @notice Computes the token0 and token1 value for a given amount of liquidity, the current
    /// pool prices and the prices at the tick boundaries
    /// @param sqrtRatioX96 A sqrt price representing the current pool prices
    /// @param sqrtRatioAX96 A sqrt price representing the first tick boundary
    /// @param sqrtRatioBX96 A sqrt price representing the second tick boundary
    /// @param liquidity The liquidity being valued
    /// @return amount0 The amount of token0
    /// @return amount1 The amount of token1
    function getAmountsForLiquidity(
        uint160 sqrtRatioX96,
        uint160 sqrtRatioAX96,
        uint160 sqrtRatioBX96,
        uint128 liquidity
    ) internal pure returns (uint256 amount0, uint256 amount1) {
        if (sqrtRatioAX96 > sqrtRatioBX96) (sqrtRatioAX96, sqrtRatioBX96) = (sqrtRatioBX96, sqrtRatioAX96);

        if (sqrtRatioX96 <= sqrtRatioAX96) {
            amount0 = getAmount0ForLiquidity(sqrtRatioAX96, sqrtRatioBX96, liquidity);
        } else if (sqrtRatioX96 < sqrtRatioBX96) {
            amount0 = getAmount0ForLiquidity(sqrtRatioX96, sqrtRatioBX96, liquidity);
            amount1 = getAmount1ForLiquidity(sqrtRatioAX96, sqrtRatioX96, liquidity);
        } else {
            amount1 = getAmount1ForLiquidity(sqrtRatioAX96, sqrtRatioBX96, liquidity);
        }
    }
}

contracts/Banks/BankBase.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "../libraries/SaferERC20.sol";
import "../utils/OwnableUpgradeable.sol";

abstract contract BankBase is Initializable, OwnableUpgradeable {
    using SaferERC20 for IERC20;

    event Mint(uint256 tokenId, address userAddress, uint256 amount);
    event Burn(uint256 tokenId, address userAddress, uint256 amount, address receiver);
    event Harvest(uint256 tokenId, address userAddress, address receiver);

    address positionsManager;
    uint256[49] __gap;

    function __BankBase_init(address _positionsManager) public onlyInitializing {
        positionsManager = _positionsManager;
        __Ownable_init();
    }

    modifier onlyAuthorized() {
        require(msg.sender == positionsManager || msg.sender == currentOwner(), "1");
        _;
    }

    function name() external pure virtual returns (string memory);

    function getIdFromLpToken(address lpToken) external view virtual returns (bool, uint256);

    function decodeId(uint256 id) external view virtual returns (address, address, uint256);

    function getUnderlyingForFirstDeposit(
        uint256 tokenId
    ) public view virtual returns (address[] memory underlying, uint256[] memory ratios) {
        underlying = new address[](1);
        underlying[0] = getLPToken(tokenId);
        ratios = new uint256[](1);
        ratios[0] = 1;
    }

    function getUnderlyingForRecurringDeposit(
        uint256 tokenId
    ) external view virtual returns (address[] memory, uint256[] memory ratios) {
        return getUnderlyingForFirstDeposit(tokenId);
    }

    function getLPToken(uint256 tokenId) public view virtual returns (address);

    function getRewards(uint256 tokenId) external view virtual returns (address[] memory rewardsArray) {
        return rewardsArray;
    }

    receive() external payable {}

    function getPendingRewardsForUser(
        uint256 tokenId,
        address user
    ) external view virtual returns (address[] memory rewards, uint256[] memory amounts) {}

    function getPositionTokens(
        uint256 tokenId,
        address user
    ) external view virtual returns (address[] memory tokens, uint256[] memory amounts);

    function mint(
        uint256 tokenId,
        address userAddress,
        address[] memory suppliedTokens,
        uint256[] memory suppliedAmounts
    ) public virtual returns (uint256);

    function mintRecurring(
        uint256 tokenId,
        address userAddress,
        address[] memory suppliedTokens,
        uint256[] memory suppliedAmounts
    ) external virtual returns (uint256) {
        return mint(tokenId, userAddress, suppliedTokens, suppliedAmounts);
    }

    function burn(
        uint256 tokenId,
        address userAddress,
        uint256 amount,
        address receiver
    ) external virtual returns (address[] memory, uint256[] memory);

    function harvest(
        uint256 tokenId,
        address userAddress,
        address receiver
    ) external virtual onlyAuthorized returns (address[] memory rewardAddresses, uint256[] memory rewardAmounts) {
        return (rewardAddresses, rewardAmounts);
    }

    function isUnderlyingERC721() external pure virtual returns (bool) {
        return false;
    }
}

contracts/Banks/ERC721Bank.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.17;

import "./ERC721Wrappers.sol";
import "./BankBase.sol";

contract ERC721Bank is Initializable, BankBase {
    using Address for address;

    struct PoolInfo {
        uint256 liquidity;
        // mapping(address=>mapping(uint=>bool)) userShares;
    }

    address[] nftManagers;
    mapping(address => address) erc721Wrappers;

    mapping(uint256 => PoolInfo) poolInfo;

    function initialize(address _positionsManager) public initializer {
        __BankBase_init(_positionsManager);
    }

    function addManager(address nftManager) external onlyOwner {
        nftManagers.push(nftManager);
    }

    function setWrapper(address nftManager, address wrapper) external onlyOwner {
        erc721Wrappers[nftManager] = wrapper;
    }

    function encodeId(uint256 id, address nftManager) public view returns (uint256) {
        for (uint256 i = 0; i < nftManagers.length; i++) {
            if (nftManagers[i] == nftManager) {
                return (i << 240) | uint240(id);
            }
        }
        revert("NFT manager not supported");
    }

    function decodeId(
        uint256 id
    ) public view override returns (address poolAddress, address nftManager, uint256 pos_id) {
        nftManager = nftManagers[id >> 240];
        pos_id = uint240(id & ((1 << 240) - 1));
        poolAddress = IERC721Wrapper(erc721Wrappers[nftManager]).getPoolAddress(nftManager, pos_id);
    }

    function getLPToken(uint256 id) public view override returns (address managerAddress) {
        (, managerAddress, ) = decodeId(id);
    }

    function getIdFromLpToken(address manager) public view override returns (bool, uint256) {
        for (uint256 i = 0; i < nftManagers.length; i++) {
            if (nftManagers[i] == manager) {
                return (true, uint160(nftManagers[i]));
            }
        }
        return (false, 0);
    }

    function name() public pure override returns (string memory) {
        return "ERC721 Bank";
    }

    function mint(
        uint256 tokenId,
        address userAddress,
        address[] memory suppliedTokens,
        uint256[] memory suppliedAmounts
    ) public override onlyAuthorized returns (uint256) {
        (, , , , , , , uint256 minted, , , , ) = INonfungiblePositionManager(suppliedTokens[0]).positions(
            suppliedAmounts[0]
        );
        poolInfo[tokenId] = PoolInfo(minted);
        return minted;
    }

    function mintRecurring(
        uint256 tokenId,
        address userAddress,
        address[] memory suppliedTokens,
        uint256[] memory suppliedAmounts
    ) external override onlyAuthorized returns (uint256) {
        (, address manager, uint256 id) = decodeId(tokenId);
        address wrapper = erc721Wrappers[manager];

        bytes memory returnData = wrapper.functionDelegateCall(
            abi.encodeWithSelector(IERC721Wrapper.deposit.selector, manager, id, suppliedTokens, suppliedAmounts)
        );
        uint256 minted = abi.decode(returnData, (uint256));
        poolInfo[tokenId].liquidity += minted;
        return minted;
    }

    function burn(
        uint256 tokenId,
        address userAddress,
        uint256 amount,
        address receiver
    ) external override onlyAuthorized returns (address[] memory outTokens, uint256[] memory tokenAmounts) {
        (, address manager, uint256 id) = decodeId(tokenId);
        address wrapper = erc721Wrappers[manager];
        bytes memory returnData = wrapper.functionDelegateCall(
            abi.encodeWithSelector(IERC721Wrapper.withdraw.selector, manager, id, amount, receiver)
        );
        (outTokens, tokenAmounts) = abi.decode(returnData, (address[], uint256[]));
        // (outTokens, tokenAmounts) = withdraw(manager, id, amount, receiver);
        (, , , , , , , uint256 liquidity, , , , ) = INonfungiblePositionManager(manager).positions(id);
        poolInfo[tokenId].liquidity = liquidity;
    }

    function harvest(
        uint256 tokenId,
        address userAddress,
        address receiver
    ) external override onlyAuthorized returns (address[] memory rewardAddresses, uint256[] memory rewardAmounts) {
        (, address manager, uint256 id) = decodeId(tokenId);
        address wrapper = erc721Wrappers[manager];
        bytes memory returnData = wrapper.functionDelegateCall(
            abi.encodeWithSelector(IERC721Wrapper.harvest.selector, manager, id, receiver)
        );
        (, , , , , , , uint256 liquidity, , , , ) = INonfungiblePositionManager(manager).positions(id);
        poolInfo[tokenId].liquidity = liquidity;
        (rewardAddresses, rewardAmounts) = abi.decode(returnData, (address[], uint256[]));
        return (rewardAddresses, rewardAmounts);
    }

    function getUnderlyingForFirstDeposit(
        uint256 tokenId
    ) public view override returns (address[] memory underlying, uint256[] memory ratios) {
        (, address manager, ) = decodeId(tokenId);
        underlying = new address[](1);
        underlying[0] = manager;
        ratios = new uint256[](1);
        ratios[0] = 1;
    }

    function getUnderlyingForRecurringDeposit(
        uint256 tokenId
    ) public view override returns (address[] memory, uint256[] memory ratios) {
        (, address manager, uint256 pos_id) = decodeId(tokenId);
        IERC721Wrapper wrapper = IERC721Wrapper(erc721Wrappers[manager]);
        return wrapper.getRatio(manager, pos_id);
        // return wrapper.getERC20Base(pool);
    }

    function getRewards(uint256 tokenId) external view override returns (address[] memory rewardsArray) {
        (rewardsArray, ) = getUnderlyingForRecurringDeposit(tokenId);
    }

    function getPendingRewardsForUser(
        uint256 tokenId,
        address user
    ) external view override returns (address[] memory rewards, uint256[] memory amounts) {
        (, address manager, uint256 pos_id) = decodeId(tokenId);
        IERC721Wrapper wrapper = IERC721Wrapper(erc721Wrappers[manager]);
        return wrapper.getRewardsForPosition(manager, pos_id);
    }

    function getPositionTokens(
        uint256 tokenId,
        address userAddress
    ) external view override returns (address[] memory outTokens, uint256[] memory tokenAmounts) {
        (, address manager, uint256 pos_id) = decodeId(tokenId);
        IERC721Wrapper wrapper = IERC721Wrapper(erc721Wrappers[manager]);
        return wrapper.getPositionUnderlying(manager, pos_id);
    }

    function isUnderlyingERC721() external pure override returns (bool) {
        return true;
    }
}

contracts/Banks/ERC721Wrappers.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.17;

import "../interfaces/UniswapV3/INonfungiblePositionManager.sol";
import "../interfaces/UniswapV3/IUniswapV3Pool.sol";
import "../interfaces/UniswapV3/IUniswapV3Factory.sol";
import "./BankBase.sol";
import "@uniswap/v3-core/contracts/libraries/TickMath.sol";
import "@uniswap/v3-periphery/contracts/libraries/LiquidityAmounts.sol";

abstract contract IERC721Wrapper is Ownable {
    function isSupported(address manager, address pool) external view virtual returns (bool);

    function getPoolAddress(address manager, uint256 id) external view virtual returns (address);

    function deposit(
        address manager,
        uint256 id,
        address[] memory suppliedTokens,
        uint256[] memory suppliedAmounts
    ) external virtual returns (uint256);

    function withdraw(
        address manager,
        uint256 id,
        uint256 amount,
        address receiver
    ) external virtual returns (address[] memory outTokens, uint256[] memory tokenAmounts);

    function harvest(
        address manager,
        uint256 id,
        address receiver
    ) external virtual returns (address[] memory outTokens, uint256[] memory tokenAmounts);

    function getRatio(
        address manager,
        uint256 id
    ) external view virtual returns (address[] memory tokens, uint256[] memory ratios);

    function getRewardsForPosition(
        address manager,
        uint256 tokenId
    ) external view virtual returns (address[] memory rewards, uint256[] memory amounts);

    function getERC20Base(address pool) external view virtual returns (address[] memory underlyingTokens);

    function getPositionUnderlying(
        address manager,
        uint256 tokenId
    ) external view virtual returns (address[] memory tokens, uint256[] memory amounts);
}

contract UniswapV3Wrapper is IERC721Wrapper {
    using SaferERC20 for IERC20;

    function isSupported(address managerAddress, address poolAddress) external view override returns (bool) {
        IUniswapV3Pool pool = IUniswapV3Pool(poolAddress);
        address token0 = pool.token0();
        address token1 = pool.token1();
        uint24 fee = pool.fee();
        INonfungiblePositionManager manager = INonfungiblePositionManager(managerAddress);
        IUniswapV3Factory factory = IUniswapV3Factory(manager.factory());
        address expectedPoolAddress = factory.getPool(token0, token1, fee);
        if (expectedPoolAddress == poolAddress) {
            return true;
        }
        return false;
    }

    function getPoolAddress(address manager, uint256 id) external view override returns (address) {
        (, , address token0, address token1, uint24 fee, , , , , , , ) = INonfungiblePositionManager(manager).positions(
            id
        );
        address factory = INonfungiblePositionManager(manager).factory();
        address poolAddress = IUniswapV3Factory(factory).getPool(token0, token1, fee);
        return poolAddress;
    }

    function deposit(
        address manager,
        uint256 id,
        address[] memory suppliedTokens,
        uint256[] memory suppliedAmounts
    ) external override returns (uint256) {
        for (uint256 i = 0; i < suppliedTokens.length; i++) {
            IERC20(suppliedTokens[i]).safeIncreaseAllowance(manager, suppliedAmounts[i]);
        }
        (, , address token0, address token1, , , , , , , , ) = INonfungiblePositionManager(manager).positions(id);
        uint256 amount0;
        uint256 amount1;
        if (token0 == suppliedTokens[0] && token1 == suppliedTokens[1]) {
            amount0 = suppliedAmounts[0];
            amount1 = suppliedAmounts[1];
        } else {
            amount1 = suppliedAmounts[0];
            amount0 = suppliedAmounts[1];
        }
        INonfungiblePositionManager.IncreaseLiquidityParams memory params = INonfungiblePositionManager
            .IncreaseLiquidityParams(id, amount0, amount1, 0, 0, block.timestamp);
        (uint256 minted, uint256 a0, uint256 a1) = INonfungiblePositionManager(manager).increaseLiquidity(params);
        address owner = INonfungiblePositionManager(manager).ownerOf(id);
        // Refund left overs
        if (token0 == suppliedTokens[0] && token1 == suppliedTokens[1]) {
            IERC20(token0).safeTransfer(owner, suppliedAmounts[0] - a0);
            IERC20(token1).safeTransfer(owner, suppliedAmounts[1] - a1);
        } else {
            IERC20(token0).safeTransfer(owner, suppliedAmounts[1] - a0);
            IERC20(token1).safeTransfer(owner, suppliedAmounts[0] - a1);
        }
        return minted;
    }

    function withdraw(
        address manager,
        uint256 id,
        uint256 amount,
        address receiver
    ) external override returns (address[] memory outTokens, uint256[] memory tokenAmounts) {
        (, , address token0, address token1, , , , , , , , ) = INonfungiblePositionManager(manager).positions(id);
        INonfungiblePositionManager.DecreaseLiquidityParams memory withdrawParams = INonfungiblePositionManager
            .DecreaseLiquidityParams(id, uint128(amount), 0, 0, block.timestamp);
        (uint256 token0Amount, uint256 token1Amount) = INonfungiblePositionManager(manager).decreaseLiquidity(
            withdrawParams
        );
        INonfungiblePositionManager.CollectParams memory params = INonfungiblePositionManager.CollectParams(
            id,
            address(this),
            2 ** 128 - 1,
            2 ** 128 - 1
        );
        INonfungiblePositionManager(manager).collect(params);
        IERC20(token0).safeTransfer(receiver, token0Amount);
        IERC20(token1).safeTransfer(receiver, token1Amount);
        outTokens = new address[](2);
        outTokens[0] = token0;
        outTokens[1] = token1;
        tokenAmounts = new uint256[](2);
        tokenAmounts[0] = token0Amount;
        tokenAmounts[1] = token1Amount;
    }

    function harvest(
        address manager,
        uint256 id,
        address receiver
    ) external override returns (address[] memory outTokens, uint256[] memory tokenAmounts) {
        (, , address token0, address token1, , , , , , , , ) = INonfungiblePositionManager(manager).positions(id);
        INonfungiblePositionManager.CollectParams memory params = INonfungiblePositionManager.CollectParams(
            id,
            receiver,
            2 ** 128 - 1,
            2 ** 128 - 1
        );
        (uint256 amount0, uint256 amount1) = INonfungiblePositionManager(manager).collect(params);
        outTokens = new address[](2);
        outTokens[0] = token0;
        outTokens[1] = token1;
        tokenAmounts = new uint256[](2);
        tokenAmounts[0] = amount0;
        tokenAmounts[1] = amount1;
    }

    function getRatio(
        address manager,
        uint256 tokenId
    ) external view override returns (address[] memory tokens, uint256[] memory ratios) {
        (
            ,
            ,
            address token0,
            address token1,
            uint24 fee,
            int24 tick0,
            int24 tick1,
            ,
            ,
            ,
            ,

        ) = INonfungiblePositionManager(manager).positions(tokenId);
        IUniswapV3Factory factory = IUniswapV3Factory(INonfungiblePositionManager(manager).factory());
        IUniswapV3Pool pool = IUniswapV3Pool(factory.getPool(token0, token1, fee));
        int24 currentTick;
        (uint160 sqrtPriceX96, , , , , , ) = pool.slot0();
        {
            currentTick = TickMath.getTickAtSqrtRatio(sqrtPriceX96);
            uint absTick = currentTick < 0 ? uint(-int(currentTick)) : uint(int(currentTick));
            uint24 tickSpacing = uint24(pool.tickSpacing());
            absTick -= absTick % tickSpacing;
            currentTick = currentTick < 0 ? -int24(int(absTick)) : int24(int(absTick));
            tokens = new address[](2);
            tokens[0] = token0;
            tokens[1] = token1;
        }
        ratios = new uint256[](2);
        {
            (uint256 amount0, uint256 amount1) = LiquidityAmounts.getAmountsForLiquidity(
                TickMath.getSqrtRatioAtTick(currentTick),
                TickMath.getSqrtRatioAtTick(tick0),
                TickMath.getSqrtRatioAtTick(tick1),
                pool.liquidity()
            );
            uint256 price;
            uint256 MAX = 2 ** 256 - 1;
            if (uint256(sqrtPriceX96) * uint256(sqrtPriceX96) > MAX / 1e18) {
                price = ((uint256(sqrtPriceX96) * uint256(sqrtPriceX96)) >> (96 * 2)) * 1e18;
            } else {
                price = (uint256(sqrtPriceX96) * uint256(sqrtPriceX96) * 1e18) >> (96 * 2);
            }
            ratios[0] = amount0;
            ratios[1] = (amount1 * 1e18) / price;
        }
    }

    function getRewardsForPosition(
        address manager,
        uint256 tokenId
    ) external view override returns (address[] memory rewards, uint256[] memory amounts) {
        (uint256 fee0, uint256 fee1) = getPendingFees(manager, tokenId);
        (, , address token0, address token1, , , , , , , , ) = INonfungiblePositionManager(manager).positions(tokenId);
        rewards = new address[](2);
        rewards[0] = token0;
        rewards[1] = token1;
        amounts = new uint256[](2);
        amounts[0] = fee0;
        amounts[1] = fee1;
    }

    function getPositionUnderlying(
        address manager,
        uint256 tokenId
    ) external view override returns (address[] memory tokens, uint256[] memory amounts) {
        (
            ,
            ,
            address token0,
            address token1,
            uint24 fee,
            int24 tickLower,
            int24 tickUpper,
            uint128 liquidity,
            ,
            ,
            ,

        ) = INonfungiblePositionManager(manager).positions(tokenId);
        IUniswapV3Pool pool = IUniswapV3Pool(
            IUniswapV3Factory(INonfungiblePositionManager(manager).factory()).getPool(token0, token1, fee)
        );
        (uint160 sqrtRatioX96, , , , , , ) = pool.slot0();
        (uint256 amount0, uint256 amount1) = LiquidityAmounts.getAmountsForLiquidity(
            sqrtRatioX96,
            TickMath.getSqrtRatioAtTick(tickLower),
            TickMath.getSqrtRatioAtTick(tickUpper),
            liquidity
        );
        tokens = new address[](2);
        tokens[0] = token0;
        tokens[1] = token1;
        amounts = new uint256[](2);
        amounts[0] = amount0;
        amounts[1] = amount1;
    }

    function getERC20Base(address poolAddress) external view override returns (address[] memory underlyingTokens) {
        IUniswapV3Pool pool = IUniswapV3Pool(poolAddress);
        address token0 = pool.token0();
        address token1 = pool.token1();
        underlyingTokens = new address[](2);
        underlyingTokens[0] = token0;
        underlyingTokens[1] = token1;
    }

    function getPendingFees(
        address manager,
        uint256 _positionId
    ) internal view returns (uint256 feeAmt0, uint256 feeAmt1) {
        int24 tickLower;
        int24 tickUpper;
        uint256 feeGrowthInside0LastX128;
        uint256 feeGrowthInside1LastX128;
        IUniswapV3Pool pool;
        {
            address token0;
            address token1;
            uint24 fee;
            (
                ,
                ,
                token0,
                token1,
                fee,
                tickLower,
                tickUpper,
                ,
                feeGrowthInside0LastX128,
                feeGrowthInside1LastX128,
                ,

            ) = INonfungiblePositionManager(manager).positions(_positionId);
            pool = IUniswapV3Pool(
                IUniswapV3Factory(INonfungiblePositionManager(manager).factory()).getPool(token0, token1, fee)
            );
        }
        (, int24 curTick, , , , , ) = pool.slot0();

        (uint128 liquidity, , , uint128 tokensOwed0, uint128 tokensOwed1) = pool.positions(
            _getPositionID(manager, tickLower, tickUpper)
        );

        feeAmt0 =
            _computeFeesEarned(pool, true, feeGrowthInside0LastX128, curTick, tickLower, tickUpper, liquidity) +
            tokensOwed0;
        feeAmt1 =
            _computeFeesEarned(pool, false, feeGrowthInside1LastX128, curTick, tickLower, tickUpper, liquidity) +
            tokensOwed1;
    }

    function _getPositionID(
        address _owner,
        int24 _lowerTick,
        int24 _upperTick
    ) internal pure returns (bytes32 positionId) {
        return keccak256(abi.encodePacked(_owner, _lowerTick, _upperTick));
    }

    // ref: from arrakis finance: https://github.com/ArrakisFinance/vault-v1-core/blob/main/contracts/ArrakisVaultV1.sol
    function _computeFeesEarned(
        IUniswapV3Pool _pool,
        bool _isZero,
        uint256 _feeGrowthInsideLast,
        int24 _tick,
        int24 _lowerTick,
        int24 _upperTick,
        uint128 _liquidity
    ) internal view returns (uint256 fee) {
        uint256 feeGrowthOutsideLower;
        uint256 feeGrowthOutsideUpper;
        uint256 feeGrowthGlobal;
        if (_isZero) {
            feeGrowthGlobal = _pool.feeGrowthGlobal0X128();
            (, , feeGrowthOutsideLower, , , , , ) = _pool.ticks(_lowerTick);
            (, , feeGrowthOutsideUpper, , , , , ) = _pool.ticks(_upperTick);
        } else {
            feeGrowthGlobal = _pool.feeGrowthGlobal1X128();
            (, , , feeGrowthOutsideLower, , , , ) = _pool.ticks(_lowerTick);
            (, , , feeGrowthOutsideUpper, , , , ) = _pool.ticks(_upperTick);
        }

        unchecked {
            // calculate fee growth below
            uint256 feeGrowthBelow;
            if (_tick >= _lowerTick) {
                feeGrowthBelow = feeGrowthOutsideLower;
            } else {
                feeGrowthBelow = feeGrowthGlobal - feeGrowthOutsideLower;
            }

            // calculate fee growth above
            uint256 feeGrowthAbove;
            if (_tick < _upperTick) {
                feeGrowthAbove = feeGrowthOutsideUpper;
            } else {
                feeGrowthAbove = feeGrowthGlobal - feeGrowthOutsideUpper;
            }

            uint256 feeGrowthInside = feeGrowthGlobal - feeGrowthBelow - feeGrowthAbove;
            fee = (_liquidity * (feeGrowthInside - _feeGrowthInsideLast)) / 2 ** 128;
        }
    }
}

contracts/interfaces/UniswapV3/INonfungi.content:
// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity >=0.7.5;
pragma abicoder v2;

import "@openzeppelin/contracts/token/ERC721/extensions/IERC721Metadata.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/IERC721Enumerable.sol";
import "./Interfaces.sol";

/// @title Non-fungible token for positions
/// @notice Wraps Uniswap V3 positions in a non-fungible token interface which allows for them to be transferred
/// and authorized.
interface INonfungiblePositionManager is
    IPoolInitializer,
    IPeripheryPayments,
    IPeripheryImmutableState,
    IERC721Metadata,
    IERC721Enumerable,
    IERC721Permit
{
    /// @notice Emitted when liquidity is increased for a position NFT
    /// @dev Also emitted when a token is minted
    /// @param tokenId The ID of the token for which liquidity was increased
    /// @param liquidity The amount by which liquidity for the NFT position was increased
    /// @param amount0 The amount of token0 that was paid for the increase in liquidity
    /// @param amount1 The amount of token1 that was paid for the increase in liquidity
    event IncreaseLiquidity(uint256 indexed tokenId, uint128 liquidity, uint256 amount0, uint256 amount1);
    /// @notice Emitted when liquidity is decreased for a position NFT
    /// @param tokenId The ID of the token for which liquidity was decreased
    /// @param liquidity The amount by which liquidity for the NFT position was decreased
    /// @param amount0 The amount of token0 that was accounted for the decrease in liquidity
    /// @param amount1 The amount of token1 that was accounted for the decrease in liquidity
    event DecreaseLiquidity(uint256 indexed tokenId, uint128 liquidity, uint256 amount0, uint256 amount1);
    /// @notice Emitted when tokens are collected for a position NFT
    /// @dev The amounts reported may not be exactly equivalent to the amounts transferred, due to rounding behavior
    /// @param tokenId The ID of the token for which underlying tokens were collected
    /// @param recipient The address of the account that received the collected tokens
    /// @param amount0 The amount of token0 owed to the position that was collected
    /// @param amount1 The amount of token1 owed to the position that was collected
    event Collect(uint256 indexed tokenId, address recipient, uint256 amount0, uint256 amount1);

    /// @notice Returns the position information associated with a given token ID.
    /// @dev Throws if the token ID is not valid.
    /// @param tokenId The ID of the token that represents the position
    /// @return nonce The nonce for permits
    /// @return operator The address that is approved for spending
    /// @return token0 The address of the token0 for a specific pool
    /// @return token1 The address of the token1 for a specific pool
    /// @return fee The fee associated with the pool
    /// @return tickLower The lower end of the tick range for the position
    /// @return tickUpper The higher end of the tick range for the position
    /// @return liquidity The liquidity of the position
    /// @return feeGrowthInside0LastX128 The fee growth of token0 as of the last action on the individual position
    /// @return feeGrowthInside1LastX128 The fee growth of token1 as of the last action on the individual position
    /// @return tokensOwed0 The uncollected amount of token0 owed to the position as of the last computation
    /// @return tokensOwed1 The uncollected amount of token1 owed to the position as of the last computation
    function positions(
        uint256 tokenId
    )
        external
        view
        returns (
            uint96 nonce,
            address operator,
            address token0,
            address token1,
            uint24 fee,
            int24 tickLower,
            int24 tickUpper,
            uint128 liquidity,
            uint256 feeGrowthInside0LastX128,
            uint256 feeGrowthInside1LastX128,
            uint128 tokensOwed0,
            uint128 tokensOwed1
        );

    struct MintParams {
        address token0;
        address token1;
        uint24 fee;
        int24 tickLower;
        int24 tickUpper;
        uint256 amount0Desired;
        uint256 amount1Desired;
        uint256 amount0Min;
        uint256 amount1Min;
        address recipient;
        uint256 deadline;
    }

    /// @notice Creates a new position wrapped in a NFT
    /// @dev Call this when the pool does exist and is initialized. Note that if the pool is created but not initialized
    /// a method does not exist, i.e. the pool is assumed to be initialized.
    /// @param params The params necessary to mint a position, encoded as `MintParams` in calldata
    /// @return tokenId The ID of the token that represents the minted position
    /// @return liquidity The amount of liquidity for this position
    /// @return amount0 The amount of token0
    /// @return amount1 The amount of token1
    function mint(
        MintParams calldata params
    ) external payable returns (uint256 tokenId, uint128 liquidity, uint256 amount0, uint256 amount1);

    struct IncreaseLiquidityParams {
        uint256 tokenId;
        uint256 amount0Desired;
        uint256 amount1Desired;
        uint256 amount0Min;
        uint256 amount1Min;
        uint256 deadline;
    }

    /// @notice Increases the amount of liquidity in a position, with tokens paid by the `msg.sender`
    /// @param params tokenId The ID of the token for which liquidity is being increased,
    /// amount0Desired The desired amount of token0 to be spent,
    /// amount1Desired The desired amount of token1 to be spent,
    /// amount0Min The minimum amount of token0 to spend, which serves as a slippage check,
    /// amount1Min The minimum amount of token1 to spend, which serves as a slippage check,
    /// deadline The time by which the transaction must be included to effect the change
    /// @return liquidity The new liquidity amount as a result of the increase
    /// @return amount0 The amount of token0 to acheive resulting liquidity
    /// @return amount1 The amount of token1 to acheive resulting liquidity
    function increaseLiquidity(
        IncreaseLiquidityParams calldata params
    ) external payable returns (uint128 liquidity, uint256 amount0, uint256 amount1);

    struct DecreaseLiquidityParams {
        uint256 tokenId;
        uint128 liquidity;
        uint256 amount0Min;
        uint256 amount1Min;
        uint256 deadline;
    }

    /// @notice Decreases the amount of liquidity in a position and accounts it to the position
    /// @param params tokenId The ID of the token for which liquidity is being decreased,
    /// amount The amount by which liquidity will be decreased,
    /// amount0Min The minimum amount of token0 that should be accounted for the burned liquidity,
    /// amount1Min The minimum amount of token1 that should be accounted for the burned liquidity,
    /// deadline The time by which the transaction must be included to effect the change
    /// @return amount0 The amount of token0 accounted to the position's tokens owed
    /// @return amount1 The amount of token1 accounted to the position's tokens owed
    function decreaseLiquidity(
        DecreaseLiquidityParams calldata params
    ) external payable returns (uint256 amount0, uint256 amount1);

    struct CollectParams {
        uint256 tokenId;
        address recipient;
        uint128 amount0Max;
        uint128 amount1Max;
    }

    /// @notice Collects up to a maximum amount of fees owed to a specific position to the recipient
    /// @param params tokenId The ID of the NFT for which tokens are being collected,
    /// recipient The account that should receive the tokens,
    /// amount0Max The maximum amount of token0 to collect,
    /// amount1Max The maximum amount of token1 to collect
    /// @return amount0 The amount of fees collected in token0
    /// @return amount1 The amount of fees collected in token1
    function collect(CollectParams calldata params) external payable returns (uint256 amount0, uint256 amount1);

    /// @notice Burns a token ID, which deletes it from the NFT contract. The token must have 0 liquidity and all tokens
    /// must be collected first.
    /// @param tokenId The ID of the token that is being burned
    function burn(uint256 tokenId) external payable;
}

contracts/interfaces/UniswapV3/Interface.content:
// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity >=0.7.5;
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";

/// @title Creates and initializes V3 Pools
/// @notice Provides a method for creating and initializing a pool, if necessary, for bundling with other methods that
/// require the pool to exist.
interface IPoolInitializer {
    /// @notice Creates a new pool if it does not exist, then initializes if not initialized
    /// @dev This method can be bundled with others via IMulticall for the first action (e.g. mint) performed against a pool
    /// @param token0 The contract address of token0 of the pool
    /// @param token1 The contract address of token1 of the pool
    /// @param fee The fee amount of the v3 pool for the specified token pair
    /// @param sqrtPriceX96 The initial square root price of the pool as a Q64.96 value
    /// @return pool Returns the pool address based on the pair of tokens and fee, will return the newly created pool address if necessary
    function createAndInitializePoolIfNecessary(
        address token0,
        address token1,
        uint24 fee,
        uint160 sqrtPriceX96
    ) external payable returns (address pool);
}

/// @title Provides functions for deriving a pool address from the factory, tokens, and the fee
library PoolAddress {
    bytes32 internal constant POOL_INIT_CODE_HASH = 0xe34f199b19b2b4f47f68442619d555527d244f78a3297ea89325f843f87b8b54;

    /// @notice The identifying key of the pool
    struct PoolKey {
        address token0;
        address token1;
        uint24 fee;
    }

    /// @notice Returns PoolKey: the ordered tokens with the matched fee levels
    /// @param tokenA The first token of a pool, unsorted
    /// @param tokenB The second token of a pool, unsorted
    /// @param fee The fee level of the pool
    /// @return Poolkey The pool details with ordered token0 and token1 assignments
    function getPoolKey(address tokenA, address tokenB, uint24 fee) internal pure returns (PoolKey memory) {
        if (tokenA > tokenB) (tokenA, tokenB) = (tokenB, tokenA);
        return PoolKey({token0: tokenA, token1: tokenB, fee: fee});
    }

    /// @notice Deterministically computes the pool address given the factory and PoolKey
    /// @param factory The Uniswap V3 factory contract address
    /// @param key The PoolKey
    /// @return pool The contract address of the V3 pool
    function computeAddress(address factory, PoolKey memory key) internal pure returns (address pool) {
        require(key.token0 < key.token1);
        pool = address(
            uint160(
                uint256(
                    keccak256(
                        abi.encodePacked(
                            hex"ff",
                            factory,
                            keccak256(abi.encode(key.token0, key.token1, key.fee)),
                            POOL_INIT_CODE_HASH
                        )
                    )
                )
            )
        );
    }
}

/// @title Immutable state
/// @notice Functions that return immutable state of the router
interface IPeripheryImmutableState {
    /// @return Returns the address of the Uniswap V3 factory
    function factory() external view returns (address);

    /// @return Returns the address of WETH9
    function WETH9() external view returns (address);
}

/// @title ERC721 with permit
/// @notice Extension to ERC721 that includes a permit function for signature based approvals
interface IERC721Permit is IERC721 {
    /// @notice The permit typehash used in the permit signature
    /// @return The typehash for the permit
    function PERMIT_TYPEHASH() external pure returns (bytes32);

    /// @notice The domain separator used in the permit signature
    /// @return The domain seperator used in encoding of permit signature
    function DOMAIN_SEPARATOR() external view returns (bytes32);

    /// @notice Approve of a specific token ID for spending by spender via signature
    /// @param spender The account that is being approved
    /// @param tokenId The ID of the token that is being approved for spending
    /// @param deadline The deadline timestamp by which the call must be mined for the approve to work
    /// @param v Must produce valid secp256k1 signature from the holder along with `r` and `s`
    /// @param r Must produce valid secp256k1 signature from the holder along with `v` and `s`
    /// @param s Must produce valid secp256k1 signature from the holder along with `r` and `v`
    function permit(address spender, uint256 tokenId, uint256 deadline, uint8 v, bytes32 r, bytes32 s) external payable;
}

/// @title Periphery Payments
/// @notice Functions to ease deposits and withdrawals of ETH
interface IPeripheryPayments {
    /// @notice Unwraps the contract's WETH9 balance and sends it to recipient as ETH.
    /// @dev The amountMinimum parameter prevents malicious contracts from stealing WETH9 from users.
    /// @param amountMinimum The minimum amount of WETH9 to unwrap
    /// @param recipient The address receiving ETH
    function unwrapWETH9(uint256 amountMinimum, address recipient) external payable;

    /// @notice Refunds any ETH balance held by this contract to the `msg.sender`
    /// @dev Useful for bundling with mint or increase liquidity that uses ether, or exact output swaps
    /// that use ether for the input amount
    function refundETH() external payable;

    /// @notice Transfers the full amount of a token held by this contract to recipient
    /// @dev The amountMinimum parameter prevents malicious contracts from stealing the token from users
    /// @param token The contract address of the token which will be transferred to `recipient`
    /// @param amountMinimum The minimum amount of token required for a transfer
    /// @param recipient The destination address of the token
    function sweepToken(address token, uint256 amountMinimum, address recipient) external payable;
}

contracts/interfaces/UniswapV3/IUniswapV.content:
// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity >=0.5.0;

/// @title The interface for the Uniswap V3 Factory
/// @notice The Uniswap V3 Factory facilitates creation of Uniswap V3 pools and control over the protocol fees
interface IUniswapV3Factory {
    /// @notice Emitted when the owner of the factory is changed
    /// @param oldOwner The owner before the owner was changed
    /// @param newOwner The owner after the owner was changed
    event OwnerChanged(address indexed oldOwner, address indexed newOwner);

    /// @notice Emitted when a pool is created
    /// @param token0 The first token of the pool by address sort order
    /// @param token1 The second token of the pool by address sort order
    /// @param fee The fee collected upon every swap in the pool, denominated in hundredths of a bip
    /// @param tickSpacing The minimum number of ticks between initialized ticks
    /// @param pool The address of the created pool
    event PoolCreated(
        address indexed token0,
        address indexed token1,
        uint24 indexed fee,
        int24 tickSpacing,
        address pool
    );

    /// @notice Emitted when a new fee amount is enabled for pool creation via the factory
    /// @param fee The enabled fee, denominated in hundredths of a bip
    /// @param tickSpacing The minimum number of ticks between initialized ticks for pools created with the given fee
    event FeeAmountEnabled(uint24 indexed fee, int24 indexed tickSpacing);

    /// @notice Returns the current owner of the factory
    /// @dev Can be changed by the current owner via setOwner
    /// @return The address of the factory owner
    function owner() external view returns (address);

    /// @notice Returns the tick spacing for a given fee amount, if enabled, or 0 if not enabled
    /// @dev A fee amount can never be removed, so this value should be hard coded or cached in the calling context
    /// @param fee The enabled fee, denominated in hundredths of a bip. Returns 0 in case of unenabled fee
    /// @return The tick spacing
    function feeAmountTickSpacing(uint24 fee) external view returns (int24);

    /// @notice Returns the pool address for a given pair of tokens and a fee, or address 0 if it does not exist
    /// @dev tokenA and tokenB may be passed in either token0/token1 or token1/token0 order
    /// @param tokenA The contract address of either token0 or token1
    /// @param tokenB The contract address of the other token
    /// @param fee The fee collected upon every swap in the pool, denominated in hundredths of a bip
    /// @return pool The pool address
    function getPool(address tokenA, address tokenB, uint24 fee) external view returns (address pool);

    /// @notice Creates a pool for the given two tokens and fee
    /// @param tokenA One of the two tokens in the desired pool
    /// @param tokenB The other of the two tokens in the desired pool
    /// @param fee The desired fee for the pool
    /// @dev tokenA and tokenB may be passed in either order: token0/token1 or token1/token0. tickSpacing is retrieved
    /// from the fee. The call will revert if the pool already exists, the fee is invalid, or the token arguments
    /// are invalid.
    /// @return pool The address of the newly created pool
    function createPool(address tokenA, address tokenB, uint24 fee) external returns (address pool);

    /// @notice Updates the owner of the factory
    /// @dev Must be called by the current owner
    /// @param _owner The new owner of the factory
    function setOwner(address _owner) external;

    /// @notice Enables a fee amount with the given tickSpacing
    /// @dev Fee amounts may never be removed once enabled
    /// @param fee The fee amount to enable, denominated in hundredths of a bip (i.e. 1e-6)
    /// @param tickSpacing The spacing between ticks to be enforced for all pools created with the given fee amount
    function enableFeeAmount(uint24 fee, int24 tickSpacing) external;
}

